AddCSLuaFile()BANK_CONFIG = BANK_CONFIG or {}local cfg = BANK_CONFIG--[[	MODELS AND SOUNDS]]//Model of the entity.cfg.Model = "models/props/cs_assault/MoneyPallet03A.mdl"//Model of the bag that is spawned when a person hits +USE on the entity.cfg.BagModel = "models/props_c17/BriefCase001a.mdl"//Sound that plays on money pickupcfg.PickupSound = "physics/body/body_medium_impact_soft6.wav"//Message the player sees when they try to rob the bank and it has no moneycfg.EmptyMessage = "There is no money in this bank!"--[[	TEXT AND BAR]]//Message displayed above bank units, substitute "%s" for amount of money in the bank, then %s for the maximum amount of money.cfg.BankString = "Holding: $%s/$%s"//Color of the bank text string abovecfg.BankStringColor = Color(255, 255, 255)//Font size of the bank text string abovecfg.BankStringSize = 120//Message displayed above the bag, substitute %i for distance in meterscfg.BagString = "Move %im"//Color of the bag text string abovecfg.BagStringColor = Color(255, 255, 255)//Font size of the bag text string abovecfg.BagStringSize = 60//Color of the bar under the holding text, displays visually how much is in the bankcfg.BarColor = Color(255, 255, 255, 225)//Color of the progress bar that shows the cooldown between next money generationcfg.ProgressColor = Color(10, 10, 10, 220)--[[	MONEY AND BAGS]]//Should we spawn in money bags when the player attempts to rob? If this is false, money is given directly to the player when they +USE the money entity.cfg.UseBags = false//Maximum amount of money each unit can holdcfg.Max = 100000//Amount in seconds before new money is generatedcfg.Delay = 30//Amount of money that gets generated every X seconds (specified in delay)cfg.AmountGenerated = 2500//Amount that is put into each bag or taken each time someone hits +USE on the entity.cfg.Amount = 15000//Amount of money the bank spawns in with.cfg.StartingAmount = 25000//Delay between picking up moneycfg.PickupDelay = 5//Distance in meters the player should take the bag away from the bank before it despawns and money is given.cfg.Distance = 300//Notification shown to the player when they withdraw a bag. Substitute %s for amount of money in the bag.cfg.RobNotification = "You have taken a bag worth $%i!"//Notification shown to the player when they actually cash a bag out. Substitute %s for amount of money they earned.cfg.WithdrawNotification = "You have stolen $%s"--[[	WANTED AND POLICE]]//Should we make the player wanted when they take money?cfg.WantOnRobbery = true//Reason for wanting the player on robbery, only if above setting is enabledcfg.WantedReason = "Robbing the Bank"//How many cop jobs should be online for people to be able to rob? Set to 0 to disablecfg.MinCopJobs = 3//These are the jobs that follow the above rule. Use job names as strings, not TEAM_ variables. These jobs are also unable to rob the bank.cfg.CopJobs = {	["President"] = true,	["Secret Service"] = true,	["FIB [T6]"] = true,	["SWAT Chief [T5]"] = true,	["SWAT Heavy [T4]"] = true,	["SWAT Sniper [T3]"] = true,	["SWAT Medic [T1]"] = true,	["SWAT"] = true,	["Sheriff"] = true,	["Police Officer"] = true,	["Highway Patrol"] = true,	["Prison Guard"] = true,		["Iron Man"] = true,	["War Machine"] = true,	["Spiderman"] = true,	["Yoda Hulk"] = true,	["Aquaman"] = true,	["Batman"] = true,	["Superman"] = true,	["Black Lightning"] = true,	["Firestorm"] = true,	["Power Ranger"] = true,	["Ninja Turtle"] = true,	["Star Lord"] = true,}//These are jobs which are exclusively allowed to rob the bank. If you make this empty, everyone will be able to rob except for CopJobs listed above.cfg.AllowedJobs = {	["Mob boss"] = true,	["Gangster"] = true,	["Thief"] = true,	["Blackhat Hacker"] = true,	["ISIS Operative [T6]"] = true,	["Terrorist Leader [T6]"] = true,	["Terrorist [T5]"] = true,	["Marauder [T4]"] = true,	["Combat Medic [T3]"] = true,	["Pro Thief [T2]"] = true,	["Pro Hacker [T1]"] = true,	["Thanos"] = true,	["Joker"] = true,	["Chucky"] = true,	["Scream"] = true,	["Howard The Alien"] = true,}//Notification sent to police when someone is robbing the bank. Substitute %s for their name.cfg.PoliceRobNotification = "%s is robbing the bank!"//Message displayed above bank units when there are not enough cops online to rob the bank. Substitute "i%" for amount of cops online, then MinCopJobs above.cfg.CannotRobCopString = "You can't rob this! %i/%i cops online."//Message displayed when you are a cop and you cannot rob the bank, substitute %s for the job namecfg.CannotRobAsCopString = "Unable to rob banks as %s!"//Should we use the alarm?cfg.UseAlarm = true//Sound that plays when the bank is robbed.cfg.AlarmSound = "ambient/alarms/alarm1.wav"--[[	COOLDOWN]]//Should we use a global cooldown?cfg.UseCooldowns = true//Time in seconds to cooldown after banks have been fully depleted, before people can rob againcfg.CooldownTime = 180//Message to show when the bank is on cooldown, substitute %i for the time in seconds before it can be robbed againcfg.CooldownString = "Cooldown! %is left"--[[	SPAWNING]]//Locations the money will spawn at, when the map is initially loaded.cfg.Locations = {	-- {		-- pos = Vector(1024, 3086, -195),		-- ang = Angle(0, 0, 0)	-- }}